package Lecture3;
import java.util.*;
public class MaximumTwoNumber {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		Scanner sc = new Scanner(System.in);
		int x = sc.nextInt();
		int y = sc.nextInt();
		
		if(x > y) {
			System.out.print(x +" x is Maximum");
		} else {
			System.out.println(y + " y is Maximum");
		}
	}

}
