package Lecture3;
import java.util.*;
public class AddTwoNumber {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		Scanner sc = new Scanner(System.in);
		
		int a = sc.nextInt(); // int
		int b = sc.nextInt();
		
		int c = a + b;
		System.out.print("The sum of "+ a + " + " + b + " = "  + c);
	}

}
